def MillerRabin(p):
    """
        Input: p integer such as p >= 5 and odd.
        
        Output: p is prime or probably prime
    """
#    pdb.set_trace()
    
    assert p >= 5    
    
    if 1 & p == 0:
        return False
    
    s = p - 1
    u = 0
    while (1 & s) == 0:
        s >>= 1
        u += 1 

    prime = False

    for _ in xrange(20):
        a = randint(2, p - 2)
        a = powerModInt(a,s,p)
        
        if a == 1 or a == p - 1:
            prime = True
            continue
        else:
            i = 1
            while i < u - 1:
                i += 1
                a = powerModInt(a,2,p)
                if a == p - 1:
                    prime = True
                    continue
                if a == 1:
                    prime = False
                    continue
#            prime = False
    return prime

